import React, { useState } from 'react';
import '../App.css';
import { InputWithButton } from './Input';

async function searchVocab(word) {
  try {
    let resp = await fetch(`https://api.dictionaryapi.dev/api/v2/entries/en/${word}`);
    if (!resp.ok) { // if HTTP-status is 200-299
      let error = await resp.text();
      throw new Error(error);
    }
    let data = await resp.json();
    return data;
  } catch (error) {
    console.error('Error:', error); // log the error to the console
    return null; // or handle the error in another appropriate way
  }
}

  function App() {

    let initial ={
      '单词': 'Hello',
      '解释': '你好',
      '词族': ['greeting','salutation']
      }

  const [vocabList,setVocabList] = useState([initial]);
  const [inputid,setinputId] = useState(0);
  const [submit,setSubmit] = useState(false);
  const [inputValue,setInputValue] = useState('');


  function handler0(){

    searchVocab(inputValue).then(data => {
      console.log(data)
      let word = data[0].word
      let meaning = data[0].meanings[0].definitions[0].definition
      let synonyms = data[0].meanings[0].definitions[0].synonyms
      let newVocab = {
        '单词': word,
        '解释': meaning,
        '词族': synonyms
      }
      setVocabList([...vocabList,newVocab])
    })
    setVocabList([...vocabList,{'单词':inputValue}]);
    setInputValue('');
  }

  function handler1(){
    console.log('Button clicked');
  }

  return (
    <> 
    <div> {
      vocabList.map((key, index) => {
        return <ul key={index}>
          {Object.entries(key).map(([key, value], index) => <li key={index}>{`${key}: ${value}`}</li>)}
        </ul>
      })
    } 
    </div>

    <div>
      <p> {inputValue}</p>
    </div>
    <InputWithButton id={inputid} value={inputValue}  type="text"  onChange={
      (e) =>{
       setInputValue(e.target.value);
    }
    } onClick0={handler0} onClick1={handler1} />




  </>

  );
}

export default App;
