import React, { useState } from 'react';
import Draggable from 'react-draggable';
import { Resizable } from 'react-resizable';
import 'react-resizable/css/styles.css';

export default function Card({vocabList}) {
    const [sizes, setSizes] = useState(vocabList.map(() => ({ width: 200, height: 200 })));

    const handleResize = (index, size) => {
        setSizes(sizes.map((s, i) => i === index ? size : s));
    };

    return vocabList.map((vocab, index) => {
        return (
            <Draggable key={index}>
                <Resizable 
                    width={sizes[index].width} 
                    height={sizes[index].height} 
                    onResize={(event, { size }) => handleResize(index, size)}
                >
                    <div className="card">
                        <div className="align">
                            <span className="red"></span>
                            <span className="yellow"></span>
                            <span className="green"></span>
                            <h1>{vocab.word}</h1>
                            <p>{vocab["解释"]}</p>
                        </div>
                    </div>
                </Resizable>
            </Draggable>
        );
    });
}
